import React from 'react';
import CountUp from 'react-countup';
import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row';

const Cards = ({ data }) => {
    if(!data.data){
        return "cargando..."
    }
    else{
        return(
            <div>
            <Row>
                <Card>
                    <Card.Body>
                        <Card.Text>
                            <CountUp 
                                start={0}
                                end={data.data.confirmed.value}
                                duration={3}
                                separator="."
                            />
                        </Card.Text>
                    </Card.Body>
                </Card>
            </Row>
        </div>
        )
    }
}

export default Cards;
